(54, 'Liên thông', 'Liên thông', '', '2021-08-30', 54, '<div class=\"entry-content clr\" itemprop=\"text\" style=\"margin: 0px 0px 20px; padding: 0px; border: 0px; outline: 0px; font-variant: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; font-family: inherit; font-size: 15px; font-style: inherit; font-weight: inherit;\"><div data-elementor-type=\"wp-post\" data-elementor-id=\"856\" class=\"elementor elementor-856\" data-elementor-settings=\"[]\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-variant: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; font-family: inherit; font-style: inherit; font-weight: inherit; hyphens: manual;\"><div class=\"elementor-inner\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-variant: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; font-family: inherit; font-style: inherit; font-weight: inherit;\"><div class=\"elementor-section-wrap\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-variant: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; font-family: inherit; font-style: inherit; font-weight: inherit;\"><section class=\"elementor-section elementor-top-section elementor-element elementor-element-3603a6c elementor-section-boxed elementor-section-height-default elementor-section-height-default\" data-id=\"3603a6c\" data-element_type=\"section\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-variant: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; font-family: inherit; font-style: inherit; font-weight: inherit; position: relative;\"><div class=\"elementor-container elementor-column-gap-default\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; font-variant: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; font-family: inherit; font-style: inherit; font-we